raw_input = raw_input.splitlines()
raw_input = [list(s) for s in raw_input]
# print(raw_input)

def solution(raw_input: list[list[str]])-> int:
    
    start_pos = (-1, -1)
    for i in range(len(raw_input)):
        for j in range(len(raw_input[i])):
            if raw_input[i][j] == "S":
                start_pos = (i, j)
                break
    if start_pos == (-1, -1):
        return -1
    memory = {}
    i, j = start_pos
    return helper(raw_input, i + 1, j, memory)
    
def helper(raw_input: list[list[str]], i: int, j: int, memory: dict)-> int:
    
    if i >= len(raw_input) or j < 0 or j >= len(raw_input[i]):
        return 0
    if i == len(raw_input) - 1:
        return 1
    if (i, j) in memory:
        return memory[(i, j)]
    if raw_input[i][j] == "^":
        res = (helper(raw_input, i + 1, j - 1, memory) + helper(raw_input, i + 1, j + 1, memory))
    else:
        res = helper(raw_input, i + 1, j, memory)
    memory[(i, j)] = res
    return res
    

print(solution(raw_input))